    private void Butt_reserveActionPerformed(java.awt.event.ActionEvent evt) {                                             
        // TODO add your handling code here:
        String [] reserve = {"예약 확인", "예약 진행", "취소"};
        int num = JOptionPane.showOptionDialog(null, "체크 기능 중 어느 것을 수행하시겠습니까?", null,
                JOptionPane.YES_NO_CANCEL_OPTION, JOptionPane.QUESTION_MESSAGE, null, reserve, reserve[2]);
        if(num == JOptionPane.YES_OPTION){
             new ReserveCheck().setVisible(true);
            }
        else if(num == JOptionPane.NO_OPTION){
             new Reservation().setVisible(true);
        }
        else {}
    }                                            

    private void Butt_checkActionPerformed(java.awt.event.ActionEvent evt) {                                           
        // TODO add your handling code here:
        String [] reserve = {"체크인", "체크아웃", "취소"};
        int num = JOptionPane.showOptionDialog(null, "체크 기능 중 어느 것을 수행하시겠습니까?", null,
                JOptionPane.YES_NO_CANCEL_OPTION, JOptionPane.QUESTION_MESSAGE, null, reserve, reserve[2]);
        if(num == JOptionPane.YES_OPTION){
             new CheckIn().setVisible(true);
            }
        else if(num == JOptionPane.NO_OPTION){
             new CheckOut().setVisible(true);
        }
        else {}
    }                                          

