    private void Butt_LogOutActionPerformed(java.awt.event.ActionEvent evt) {                                            
        // TODO add your handling code here:
        int a = JOptionPane.showConfirmDialog(null, "로그아웃하시겠습니까?","Logout?", JOptionPane.YES_NO_OPTION);
        if(a == JOptionPane.CLOSED_OPTION){}
        else if(a == JOptionPane.YES_OPTION){
            JOptionPane.showMessageDialog(null, "로그아웃을 합니다");
            new Login().setVisible(true);
            dispose();
        }
        else {}
    }                                           

